import axios from 'axios';
import { BASE_URL } from '../../Constants/url';


export default function TelaList() {
  const [matches, setMatches] = useState([])
  useEffect(() =>{
    getMatches()
  }, [])

  const getMatches = () => {
    axios
      .get(
        `${BASE_URL}matches`

      )
      .then(res => {
       console.log(res.data.matches);
       setMatches(res.data.matches)

      })
      .catch(err => {
        console.log(err.response.data);
      })


    }
    const deleteMatch =()=> {
      axios
        .put(`${BASE_URL}clear`)
        .then((res) => {
         alert("Apagou os metches todos!")
        })
        .catch((error) => {
          alert(error.response.data)
        })
    }

  const render =matches.map((match)=>{
    return(
      <div key={match.id}>
        <img src={match.photo} alt={match.name} />
        <p>{match.name}</p>

      </div>
    )
  })



  return (
    <>

    {/* <img className="arrox-doce"  src={matches.photo} alt={matches.name}/>
            <h2  className="arrox">{matches.name}</h2> */}
            {render}
            <button onClick={deleteMatch()}>apagar match</button>
    </>
  )
}
