import React, {useState, useEffect}  from 'react';
import axios from 'axios';
import "./TelaInfo.css"
import { BASE_URL } from '../../Constants/url';
import {useNavigate} from 'react-router-dom'
import {goTelaList} from '../../routes/coordinator'

import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';






export default function TelaInfo() {
  const navigate = useNavigate()

  const [profile, setProfile] = useState({})


  useEffect(()=>{
   getProfileChoose()

  },[])


 const getProfileChoose =()=> {
   axios
     .get(`${BASE_URL}person`)
     .then((res) => {
       setProfile(res.data.profile)
     })
     .catch((error) => {
       alert(error.response.data)
     })
 }
  const choosePerson =(id,boolean)=>{
    const body ={
        id:id,
        choice:boolean,
    }
    axios.post(`${BASE_URL}choose-person`,body)
    .then((res)=>{
        getProfileChoose()
    })
    .cath((err)=>{
        alert(err.response.data)
    })
}

    return(
        <div className="tela">
           <img className="imagem"  src={profile.photo} alt={profile.name}/>
          <section className="info" >
            <h2  className="arrox">{profile.name}</h2>
            <h2  className="arrox">{profile.age} </h2>
            <p className="arrox"> {profile.bio}</p>
          </section>
          <div className="botoes">
            <button className="botao1" onClick={()=>choosePerson(profile.id,false)} > x </button>
            <button className="botao2"onClick={()=>choosePerson(profile.id,true)} > ♥ </button>
            <button onClick={()=> goTelaList(navigate)} > tela list</button>


      <Card sx={{ minWidth: 275 }}>
      <CardContent>
        <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>

        </Typography>
        <Typography variant="h5" component="div">
        {profile.name}
        </Typography>
        <Typography sx={{ mb: 1.5 }} color="text.secondary">
          adjective
        </Typography>
        <Typography variant="body2">
          well meaning and kindly.
          <br />
          {'"a benevolent smile"'}
        </Typography>
      </CardContent>

    </Card>

          </div>
        </div>


    )
}
