import React, {useState, useEffect}  from 'react';
import axios from 'axios';
import './App.css';
import TelaInfo from './TelaInfo'
import TelaList from './TelaList'
import { BASE_URL } from './Constants/url';
import styled from 'styled-components';


const Tela = styled.div`
  background: lightgrey;
  border-radius: 10px;
  width: 400px;
  height: 600px;
  display: flex;
  flex-direction: column;
  justify-content:center ;
  align-items: center;
  text-align: center;


`

export default function App() {
  const [tela, setTela] = useState("info")

  const Clear =()=> {
    axios
      .put(`${BASE_URL}clear`)
      .then((res) => {
       alert("Apagou os metches todos!")
      })
      .catch((error) => {
        alert(error.response.data)
      })
  }
  const trocaTela = () => {
    if (tela === "info") {
      setTela("list")
    } else {
      setTela("info")
    }
  };
  return (
    <Tela>
       <button onClick={trocaTela}>Trocar de tela</button>
       <button onClick={Clear}>Apagar matches</button>
        {tela === "info" ? <TelaInfo /> : <TelaList/>}

    </Tela>
  );
}
