import React, {useState, useEffect}  from 'react';
import axios from 'axios';
import { BASE_URL } from './Constants/url';
import styled from 'styled-components';

const Button = styled.button`
  background: transparent;
  border-radius: 3px;
  border: 2px solid palevioletred;
  color: palevioletred;
  margin: 0 1em;
  padding: 0.25em 1em;
`

export default function TelaInfo() {

  const [profile, setProfile] = useState({})


  useEffect(()=>{
   getProfileChoose()

  },[])


 const getProfileChoose =()=> {
   axios
     .get(`${BASE_URL}person`)
     .then((res) => {
       setProfile(res.data.profile)
     })
     .catch((error) => {
       alert(error.response.data)
     })
 }
  const choosePerson =(id,boolean)=>{
    const body ={
        id:id,
        choice:boolean,
    }
    axios.post(`${BASE_URL}choose-person`,body)
    .then((res)=>{
        getProfileChoose()
    })
    .cath((err)=>{
        alert(err.response.data)
    })
}

    return(
        <div>
            <img className="arrox-doce"  src={profile.photo} alt={profile.name}/>
            <h2  className="arrox">{profile.name}</h2>
            <h2  className="arrox">{profile.age} </h2>
            <h2  className="arrox"> {profile.bio}</h2>

            <Button onClick={()=>choosePerson(profile.id,false)} > x </Button>
            <Button onClick={()=>choosePerson(profile.id,true)} > â™¥ </Button>

        </div>


    )
}
