import React, {useState, useEffect}  from 'react';
import axios from 'axios';
import { BASE_URL } from './Constants/url';
import styled from 'styled-components';

const Button = styled.button`

  background: transparent;
  border-radius: 3px;
  border: 2px solid palevioletred;
  color: palevioletred;
  margin: 0 1em;
  padding: 0.25em 1em;
`
const Tela = styled.div`
  background: lightgrey;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
  border-radius: 10px;
  width: 400px;
  height: 600px;
  display: flex;
  flex-direction: column;
  justify-content:center ;
  align-items: center;
  text-align: center;


`
const Imagem = styled.img`

  border-radius: 50%;
  border: white 2px;
  width: 20em;
  height: 20em;



`
const Card = styled.section`


  border: 2px;




`



export default function TelaInfo() {

  const [profile, setProfile] = useState({})


  useEffect(()=>{
   getProfileChoose()

  },[])


 const getProfileChoose =()=> {
   axios
     .get(`${BASE_URL}person`)
     .then((res) => {
       setProfile(res.data.profile)
     })
     .catch((error) => {
       alert(error.response.data)
     })
 }
  const choosePerson =(id,boolean)=>{
    const body ={
        id:id,
        choice:boolean,
    }
    axios.post(`${BASE_URL}choose-person`,body)
    .then((res)=>{
        getProfileChoose()
    })
    .cath((err)=>{
        alert(err.response.data)
    })
}

    return(
        <Tela>
           <Imagem className="arrox-doce"  src={profile.photo} alt={profile.name}/>
          <Card>
            <h2  className="arrox">{profile.name}</h2>
            <h2  className="arrox">{profile.age} </h2>
            <p className="arrox"> {profile.bio}</p>
          </Card>
            <Button onClick={()=>choosePerson(profile.id,false)} > x </Button>
            <Button onClick={()=>choosePerson(profile.id,true)} > â™¥ </Button>

        </Tela>


    )
}
