import React, {useState, useEffect}  from 'react';
import axios from 'axios';
import './App.css';
import TelaInfo from './TelaInfo'
import TelaList from './TelaList'
import { BASE_URL } from './Constants/url';
import styled from 'styled-components';


const Button = styled.button`
  background: transparent;
  border-radius: 3px;
  border: 2px solid palevioletred;
  color: palevioletred;
  margin: 0 1em;
  padding: 0.25em 1em;
`

export default function App() {
  const [tela, setTela] = useState("info")

  const Clear =()=> {
    axios
      .put(`${BASE_URL}clear`)
      .then((res) => {
       alert("Apagou os metches todos!")
      })
      .catch((error) => {
        alert(error.response.data)
      })
  }
  const trocaTela = () => {
    if (tela === "info") {
      setTela("list")
    } else {
      setTela("info")
    }
  };
  return (
    <div>
       <Button onClick={trocaTela}>Trocar de tela</Button>
       <button onClick={Clear}>Apagar matches</button>
        {tela === "info" ? <TelaInfo /> : <TelaList/>}

    </div>
  );
}
